var app = angular.module("myApp", []);
app.factory("$calc", function () {
    return {
        add: function (a, b) {
            return a + b
        }
    }
});
app.controller("myCtrl", ["$scope", "$calc", function (a, b) {
    a.result = 0;
    a.x = 0;
    a.y = 0;
    a.add = function () {
        a.result = b.add(a.x, a.y)
    }
}]);